    public void gettiemp(LatLng origen, LatLng destino){
        JsonParser parser = new JsonParser();

        String ori = origen.latitude+","+origen.longitude;
        String des = destino.latitude+","+destino.longitude;

        Thread thread= new Thread(new Runnable() {
            @Override
            public void run() {

                String url="https://maps.googleapis.com/maps/api/distancematrix/json?transit_mode=bus&traffic_model=pessimistic&departure_time=now&origins="+ori+"&destinations="+des+"&key="+ api;
                JsonParser parser = new JsonParser();

                try {
                    Request request = new Request.Builder()
                            .url(url)
                            .build();
                    okhttp3.Response response = client.newCall(request).execute();
                    f=response.body().string();

                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        });
        thread.start();

        try {
            thread.join();
        } catch (InterruptedException e) {
            e.printStackTrace();
        }

        Object obj = parser.parse(f);
        JsonObject jsonobj= (JsonObject) obj;

        JsonArray dist=(JsonArray)jsonobj.get("rows");
        JsonObject obj2 = (JsonObject)dist.get(0);
        JsonArray disting=(JsonArray)obj2.get("elements");
        JsonObject obj3 = (JsonObject)disting.get(0);
        JsonObject obj4=(JsonObject)obj3.get("distance");
        JsonObject obj5=(JsonObject)obj3.get("duration_in_traffic");

        tiempo = String.valueOf(obj5.get("text"));
    }

